mapper.select:
# 这是一个三表联查 select ai.id, ai.name, ai.introduction, ai.district_id, ai.district_name, ai.city_id, ai.city_name, ai.province_id, ai.province_name, ai.address_detail, ai.latitude, ai.longitude, ai.phone, ai.is_release, ifnull(tc.room_count, 0) total_room_count, ifnull(tc.room_count, 0) - ifnull(cc.leased_room_count,0) as free_room_count from ( select id, name, introduction, district_id, district_name, city_id, city_name, province_id, province_name, address_detail, latitude, longitude, phone, is_release from apartment_info
is_deleted = 0
and province_id = #{queryVo.provinceId}
and city_id = #{queryVo.cityId}
and district_id = #{queryVo.districtId}
)ai left join ( select apartment_id, count(*) as room_count from room_info where is_deleted != 0 and is_release = 1 group by apartment_id )tc on ai.id = tc.apartment_id left join ( select apartment_id, count(*) as leased_room_count from lease_agreement where is_deleted != 0 and status in (2,5) group by apartment_id )cc # check_in count on ai.id = cc.apartment_id
